/**
 * Module dependencies.
 */
var passport = require('passport')
  , util = require('util')
  , BadRequestError = require('./errors/badrequesterror');


/**
 * `Strategy` constructor.
 *
 * The cookie authentication strategy authenticates requests based on the
 * username / userid stored in a cookie.
 *
 * Applications must supply a `verify` callback which accepts `username` and `userid`
 * and then calls the `done` callback supplying a
 * `user`, which should be set to `false` if the credentials are not valid.
 * If an exception occured, `err` should be set.
 *
 * Optionally, `options` can be used to change the cookies in which the
 * credentials are found.
 *
 * Options:
 *   - `usernameCookie`  cookie name where the username is found, defaults to _username_
 *   - `useridCookie`  cookie name where the userid is found, defaults to _userid_
 *   - `passReqToCallback`  when `true`, `req` is the first argument to the verify callback (default: `false`)
 *
 * Examples:
 *
 *     passport.use(new LocalStrategy(
 *       function(username, userid, done) {
 *         User.findOne({ username: username, userid: userid }, function (err, user) {
 *           done(err, user);
 *         });
 *       }
 *     ));
 *
 * @param {Object} options
 * @param {Function} verify
 * @api public
 */
function Strategy(options, verify) {
  if (typeof options == 'function') {
    verify = options;
    options = {};
  }
  if (!verify) throw new Error('local authentication strategy requires a verify function');

  this._usernameCookie = options.usernameCookie || 'username';
  this._useridCookie = options.useridCookie || 'userid';

  passport.Strategy.call(this);
  this.name = 'cookie';
  this._verify = verify;
  this._passReqToCallback = options.passReqToCallback;
}

/**
 * Inherit from `passport.Strategy`.
 */
util.inherits(Strategy, passport.Strategy);

/**
 * Authenticate request based on the contents of a form submission.
 *
 * @param {Object} req
 * @api protected
 */
Strategy.prototype.authenticate = function(req, options) {
  options = options || {};
  var username = req.cookies[this._usernameCookie];
  var userid = req.cookies[this._useridCookie];

  if (username == '0' || userid == '0' || ! username || ! userid) {
    return this.fail(new BadRequestError(options.badRequestMessage || 'Missing credentials'));
  }

  var self = this;

  function verified(err, user, info) {
    if (err) { return self.error(err); }
    if (!user) { return self.fail(info); }
    self.success(user, info);
  }

  if (self._passReqToCallback) {
    this._verify(req, username, userid, verified);
  } else {
    this._verify(username, userid, verified);
  }
}


/**
 * Expose `Strategy`.
 */
module.exports = Strategy;
